import React, { Component } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import Link from 'next/link';
import DeleteRestaurant from './DeleteRestaurant';
import UpdateRestaurant from './UpdateRestaurant';
import { CURRENT_USER_QUERY } from './User';
import { Query } from 'react-apollo';

const Flex = styled.div`
  display:flex;
  justify-content:space-around;
`;

const RestaurantsList = styled.div`
  border: 1px solid #f4976c;
  padding: 2%;
  margin:0 5%;
  color:#f4976c;

  :hover {
    box-shadow: 10px 10px 5px grey;
  }
`;

const RestaurantTitle = styled.h2`
  border: 1px solid #00CCCC;
  margin: 0 45% 1% 0%;
`;

const RestaurantImg = styled.img`
  width:350px;
  height:250px;
  background-size:contain;
`;

const RestaurantDescription = styled.h2`
  width:50%;
`;

export default class Restaurant extends Component {
  static propTypes = {
    restaurant: propTypes.object.isRequired
  }
  render() {
    const { restaurant } = this.props;
    console.log(restaurant.user.id, 'restaurant')
    return (
      <RestaurantsList>
        <div>
          <Query query={CURRENT_USER_QUERY}>
            {({ data, loading }) => {
              console.log(data)
              // <UpdateRestaurant id={restaurant.id} />
            }}
          </Query>
          <Link href={{
            pathname: '/restaurant',
            query: { id: restaurant.id }
          }}>
            <a>
              <RestaurantTitle>{restaurant.name}</RestaurantTitle>
            </a>
          </Link>
          <Flex>
            <RestaurantDescription>{restaurant.description}</RestaurantDescription>
            <RestaurantImg src={restaurant.image} alt={restaurant.name} />
          </Flex>
        </div>
      </RestaurantsList>
    )
  }
}
