import React, { Component } from 'react'
import { Query } from 'react-apollo';
import gql from 'graphql-tag';

const SINGLE_RESTAURANT_QUERY = gql`
  query SINGLE_RESTAURANT_QUERY($id:ID!){
    restaurant(where:{id:$id}){
      id
      name
      description
      location
      image
    }
  }
`;

class SingleRestaurant extends Component {
  render() {
    return (
      <Query
        query={SINGLE_RESTAURANT_QUERY}
        variables={{ id: this.props.id, }}>
        {({ error, loading, data }) => {
          if (error) return <h1>Error</h1>;
          if (loading) return <h1>Loading...</h1>;
          if (!error.item) return <h1>No Restaurant Found for {this.props.id}</h1>
          return <h2>Single Restaurant Component |||| {this.props.id}</h2>;
        }}
      </Query>
    )
  }
}

export default SingleRestaurant
