import React, { Component } from 'react';
import { Mutation } from 'react-apollo';
import gql from 'graphql-tag';
import Router from 'next/router';
import styled from 'styled-components';

const UPDATE_RESTAURANT_MUTATION = gql`
  mutation UPDATE_RESTAURANT_MUTATION(
    $name: String!
    $location: String!
    $description: String!
    $image: String
  ) {
    updateRestaurant(
      name:$name
      location:$location
      description:$description
      image:$image
    ) {
      id
    }
  }
`;

const Form = styled.form`
  display:flex;
  justify-content:center;
  fieldset {
    border-color:#f4976c;
    box-shadow: 10px 10px 5px grey;
    border-bottom:none;
    display:flex;
    padding:5%;
    margin:5%;
  }
  label{
    font-size:3.4rem;
    color:#537E80;
  }
  input {
      border: none;
      border-bottom: 2px dashed #9FCACC;
      background: #D2FDFF;
  }

  textarea{
      resize: none;
  }

  button {
    background:#ECFFFF;
    border:none;
    border-radius:5%;
    font-size:3.5rem;
    font-family:'Porton';
    color:#F4976C;
  }

  button:hover{
    background:#F4976C;
    color:#ECFFFF;
  }
`;

class UpdateRestaurant extends Component {
  state = {
    name: "",
    location: "",
    description: "",
    image: "",
  }

  handleChange = (e) => {
    const { name, type, value } = e.target;
    this.setState({ [name]: value })
  }

  render() {
    return (
      <Mutation mutation={UPDATE_RESTAURANT_MUTATION} variables={this.state}>
        {(updateRestaurant, { error, loading }) => (
          <Form onSubmit={async e => {
            e.preventDefault();
            const res = await createRestaurant();
            Router.push({
              pathname: '/restaurant',
              query: { id: res.data.createRestaurant.id }
            })

          }}>
            {error && <p>{error.message}</p>}
            <fieldset disabled={loading}>

              <label htmlFor="name">
                Restaurant Name
                <input
                  type="text"
                  id="name"
                  name="name"
                  placeholder="name"
                  required
                  value={this.state.name}
                  onChange={this.handleChange}
                />
              </label>

              <label htmlFor="location">
                Restaurant Location
        <input
                  type="text"
                  id="location"
                  name="location"
                  placeholder="address | city,state"
                  required
                  value={this.state.location}
                  onChange={this.handleChange}
                />
              </label>

              <label htmlFor="description">
                Restaurant Description
        <textarea
                  type="text"
                  id="description"
                  name="description"
                  placeholder="describe your restaurant in 50ish words or less"
                  maxLength="360"
                  required
                  rows="5" cols="50"
                  value={this.state.description}
                  onChange={this.handleChange}
                />
              </label>
              <button type="submit">Submit</button>
            </fieldset>
          </Form>
        )}
      </Mutation>
    )
  }
}

export default UpdateRestaurant;
export { UPDATE_RESTAURANT_MUTATION }
