import React, { Component } from 'react';
import { Mutation } from 'react-apollo';
import gql from 'graphql-tag';
import Router from 'next/router';

const CREATE_RESTAURANT_MUTATION = gql`
  mutation CREATE_RESTAURANT_MUTATION(
    $name: String!
    $location: String!
    $description: String!
    $image: String
  ) {
    createRestaurant(
      name:$name
      location:$location
      description:$description
      image:$image
    ) {
      id
    }
  }
`;

class CreateRestaurant extends Component {
  state = {
    name: "",
    location: "",
    description: "",
    image: ""
  }

  handleChange = (e) => {
    const { name, type, value } = e.target;
    this.setState({ [name]: value })
  }

  render() {
    return (
      <Mutation mutation={CREATE_RESTAURANT_MUTATION} variables={this.state}>
        {(createRestaurant, { error, loading }) => (
          <form onSubmit={async e => {
            e.preventDefault();
            const res = await createRestaurant();
            Router.push({
              pathname: '/restaurant',
              query: { id: res.data.createRestaurant.id }
            })

          }}>
            {error && <p>{error.message}</p>}
            <fieldset disabled={loading}>
              <label htmlFor="name">
                Restaurant Name
        <input
                  type="text"
                  id="name"
                  name="name"
                  placeholder="Name"
                  required
                  value={this.state.name}
                  onChange={this.handleChange}
                />
              </label>

              <label htmlFor="location">
                Restaurant Location
        <input
                  type="text"
                  id="location"
                  name="location"
                  placeholder="address | city,state"
                  required
                  value={this.state.location}
                  onChange={this.handleChange}
                />
              </label>

              <label htmlFor="description">
                Restaurant Description
        <textarea
                  type="text"
                  id="description"
                  name="description"
                  placeholder="Enter a description"
                  maxLength="51"
                  required
                  value={this.state.description}
                  onChange={this.handleChange}
                />
              </label>
              <button type="submit">Submit</button>
            </fieldset>
          </form>
        )}
      </Mutation>
    )
  }
}

export default CreateRestaurant;
export { CREATE_RESTAURANT_MUTATION }
